import React, { useContext } from 'react';
import { dataContext } from '../context/DataContext';
import vicera from '../../multimedia/gorra.png';
import './Productos.css';

function Producto() {
  const { cart, setCart } = useContext(dataContext);

  const agregarAlCarrito = (producto) => {
    setCart([...cart, producto]);
  };

  return data.map((producto) => {
    return (
      <div className="card" key={producto.id}>
        <img src={vicera} height="150px" width="20%" alt="gorrita" />
        <h3 className="Producto-estilo">{producto.firstname}</h3>
        <h4 className="Producto-estilo">{producto.Price}$</h4>
        <button onClick={() => agregarAlCarrito(producto)}>Buy</button>
      </div>
    );
  });
}

export default Producto;
