import React from 'react';
import vicera from '../../multimedia/gorra.png';

import { useContext, useState, useEffect } from "react";
import { dataContext } from "../Context/DataContext";
import axios from "axios";

import "./Producto.css";

const Producto = () => {
  const [data, setData] = useState([]);
  const { buyProducto } = useContext(dataContext);

  useEffect(() => {
    axios.get("http://localhost:3001/producto").then((res) => setData(res.data));
  }, []);

  return data.map((producto) => {
    return (
      <div className='card' key={producto.id}>
        <img src={vicera} height="150px" width="20%" alt="gorrita" />
        <h3>{producto.name}</h3>
        <h4>{producto.price}$</h4>
        <button onClick={() => buyProducto(producto)}>Buy</button>
      </div>
    );
  });
};

export default Producto;
