import React, { useState } from 'react';
import vicera from '../../multimedia/gorra.png';
import Carrito from '../carrito'

function Producto() {
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = () => {
    const nuevoProducto = {
      nombre: 'Producto 1', // Puedes mejorar esto según tus necesidades
      precio: 20.0,          // Puedes mejorar esto según tus necesidades
    };
    setCarrito([...carrito, nuevoProducto]);
  };

  return (
    <div className="producto">
      <img src={vicera} height="150px" width="20%" alt="gorrita" />
      <h3>Producto 1</h3>
      <p>Precio: $20.00</p>
      <p>Descripción: Este es un producto de ejemplo.</p>
      <button className="boton4" onClick={agregarAlCarrito}>Ir a Pagar</button>
      <Carrito productos={carrito} />
    </div>
  );
}

export default Producto;
