import React, { useState } from 'react';
import vicera from '../../multimedia/gorra.png';
import Carrito from '../carrito'

function Producto() {
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = () => {
    const nuevoProducto = { nombre: 'Producto 1', precio: 20.00 };
    setCarrito([...carrito, nuevoProducto]);
  };

  const eliminarDelCarrito = (index) => {
    const nuevoCarrito = [...carrito];
    nuevoCarrito.splice(index, 1);
    setCarrito(nuevoCarrito);
  };

  return (
    <div className="contenedor">
      <div className="producto">
        <img src={vicera} height="150px" width="20%" alt="gorrita" />
        <h3>Producto 1</h3>
        <p>Precio: $20.00</p>
        <p>Descripci√≥n: Este es un producto de ejemplo.</p>
        <button className="boton4" onClick={agregarAlCarrito}>
          Agregar al Carrito
        </button>
      </div>

      <Carrito productos={carrito} eliminarProducto={eliminarDelCarrito} />
    </div>
  );
}

export default Producto;
