import React, { useState } from 'react';
import vicera from '../../multimedia/gorra.png';

function Producto() {
  // Estado del carrito
  const [carrito, setCarrito] = useState([]);

  // Función para agregar productos al carrito
  const agregarAlCarrito = () => {
    // Lógica para agregar el producto al carrito
    const nuevoProducto = {
      nombre: 'Producto 1',
      precio: 20.00,
      descripcion: 'Este es un producto de ejemplo.',
    };

    // Actualizar el estado del carrito
    setCarrito([...carrito, nuevoProducto]);

    // Lógica adicional, como enviar datos al servidor
    enviarDatosAlServidor(nuevoProducto);
  };

  // Función para enviar datos al servidor (ejemplo)
  const enviarDatosAlServidor = (producto) => {
    // Aquí puedes realizar una solicitud HTTP para enviar los datos al servidor
    console.log('Enviando datos al servidor:', producto);
    // Puedes utilizar fetch u otra biblioteca para realizar la solicitud al servidor.
  };

  return (
    <div className="producto">
      <img src={vicera} height="150px" width="20%" alt="gorrita" />
      <h3>Producto 1</h3>
      <p>Precio: $20.00</p>
      <p>Descripción: Este es un producto de ejemplo.</p>
      <button onClick={agregarAlCarrito}>Agregar al carrito</button>
    </div>
  );
}

export default Producto;
