// Producto.jsx
import React from 'react';
import vicera from '../../multimedia/gorra.png';

function Producto({ addItem }) {
  const producto = {
    name: 'Gorra un verano sin ti',
    price: 20.00,
    description: 'Este es un producto de ejemplo.',
  };

  const handleComprar = () => {
    addItem(producto);
  };

  return (
    <div className="producto">
      <img src={vicera} height="150px" width="20%" alt="gorrita" />
      <h3>{producto.name}</h3>
      <p>Precio: ${producto.price.toFixed(2)}</p>
      <p>Descripci√≥n: {producto.description}</p>
      <button className="comprarButton" onClick={handleComprar}>
        Comprar
      </button>
    </div>
  );
}

export default Producto;
