import React, { useState } from 'react';
import './ca'

const Carrito = () => {
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
  };

  const eliminarDelCarrito = (index) => {
    const nuevoCarrito = [...carrito];
    nuevoCarrito.splice(index, 1);
    setCarrito(nuevoCarrito);
  };

  const pagar = () => {
    alert('Pago completado. Gracias por su compra.');
    setCarrito([]); // Limpiar el carrito después de realizar el pago
  };

  return (
    <div className="carrito-container">
      <h2>Carrito de Compras</h2>
      <ul>
        {carrito.map((producto, index) => (
          <li className="lista1" key={index}>
            {producto.nombre} - ${producto.precio}
            <button className="boton4" onClick={() => eliminarDelCarrito(index)}>
              Eliminar
            </button>
          </li>
        ))}
      </ul>
      <button className="boton4" onClick={() => agregarAlCarrito({ nombre: 'Producto 1', precio: 20.00 })}>
        Agregar Producto
      </button>
      <button className="boton4" onClick={pagar}>
        Ir a Pagar
      </button>
    </div>
  );
};

export default Carrito;
