import React, { useState } from 'react';
import vicera from '../../multimedia/gorra.png';

function Producto() {
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = () => {
    // Aquí puedes agregar el producto actual al carrito
    setCarrito([...carrito, { nombre: 'Producto 1', precio: 20.00 }]);
  };

  return (
    <div className="producto">
      <img src={vicera} height="150px" width="20%" alt="gorrita" />
      <h3>Producto 1</h3>
      <p>Precio: $20.00</p>
      <p>Descripción: Este es un producto de ejemplo.</p>
      <button className="boton4" onClick={agregarAlCarrito}>
        Agregar al Carrito
      </button>
    </div>
  );
}

export default Producto;
