// Carrito.js
import React, { useState } from 'react';

function Carrito({ productos, onCompraExitosa }) {
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
  };

  const eliminarDelCarrito = (producto) => {
    const nuevoCarrito = carrito.filter((p) => p !== producto);
    setCarrito(nuevoCarrito);
  };

  const realizarCompra = () => {
    // Aquí puedes realizar cualquier lógica adicional antes de la compra
    // Por ejemplo, enviar una solicitud al servidor para procesar el pago.

    // Después de realizar la compra, limpiamos el carrito y mostramos el mensaje de compra exitosa.
    setCarrito([]);
    onCompraExitosa();
  };

  return (
    <div className="carrito">
      <h2>Carrito de Compras</h2>
      <ul>
        {carrito.map((producto, index) => (
          <li key={index}>
            {producto}{' '}
            <button onClick={() => eliminarDelCarrito(producto)}>Eliminar</button>
          </li>
        ))}
      </ul>
      <button onClick={realizarCompra}>Realizar Compra</button>
    </div>
  );
}

export default Carrito;
