// Producto.js
import React, { useState } from 'react';
import Carrito from './Carrito';
import vicera from '../../multimedia/gorra.png';

function Producto() {
  const [compraExitosa, setCompraExitosa] = useState(false);
  const [carrito, setCarrito] = useState([]);

  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
  };

  const handleCompraExitosa = () => {
    setCompraExitosa(true);
  };

  return (
    <div className="producto">
      <img src={vicera} height="150px" width="20%" alt="gorrita" />
      <h3>Producto 1</h3>
      <p>Precio: $20.00</p>
      <p>Descripción: Este es un producto de ejemplo.</p>
      <button className="boton4" onClick={() => agregarAlCarrito('Producto 1')}>
        Agregar al Carrito
      </button>

      {compraExitosa && <p>La compra ha sido exitosa. ¡Gracias por tu compra!</p>}

      <Carrito productos={carrito} onCompraExitosa={handleCompraExitosa} />
    </div>
  );
}

export default Producto;
