import React, { useContext, useState } from 'react';
import axios from 'axios';
import { dataContext } from '../context/DataContext';
import Producto from './Producto'; // Asegúrate de importar el componente Producto adecuadamente
import CartElements from './CartElements';
import CartTotal from './CartTotal';
import { Link } from 'react-router-dom';

const Cart = () => {
  const { cart, setCart } = useContext(dataContext);
  const [cartItems, setCartItems] = useState([]);

  const filteredCart = Object.values(setCart).map(({ id, quantity }) => ({ id, quantity }));
  const jsonifiedCart = JSON.stringify(filteredCart);

  const handleCart = async () => {
    if (jsonifiedCart !== '[]') {
      try {
        await axios.post('http://localhost:3000/api/cart', { jsonifiedCart });
        alert('Artículos comprados con éxito');
      } catch (err) {
        alert('Error al realizar la compra');
        console.log('Error al registrar carrito: ', err);
      }
    } else {
      alert('Seleccionar artículos antes de realizar la compra');
    }
  };

  return cart.length > 0 ? (
    <>
      {cart.map((producto) => (
        <Producto key={producto.id} producto={producto} />
      ))}
      <CartElements />
      <CartTotal />

      <button className="mainBtn" onClick={handleCart}>
        Comprar
      </button>
    </>
  ) : (
    <h2 className="cart-massage-center">El carrito está vacío</h2>
  );
};

export default Cart;
