import React, { useContext } from 'react';
import { dataContext } from '../context/DataContext';
import vicera from '../../multimedia/gorra.png'
// import './Producto.css'; // Asegúrate de importar el archivo CSS correspondiente

function Producto({ producto }) {
  const { cart, setCart } = useContext(dataContext);

  const agregarAlCarrito = () => {
    setCart([...cart, producto]);
  };

  return (
    <div className="producto">
      <img src={producto.img} alt="imagen-producto" />
      <h3>{producto.firstname}</h3>
      <p>Precio: ${producto.Price}</p>
      {/* Puedes agregar más detalles si los tienes, como descripción */}
      <button className="boton4" onClick={agregarAlCarrito}>
        Agregar al Carrito
      </button>
    </div>
  );
}

export default Producto;
